import React, { useState } from 'react';
import Lottie from "lottie-react";
import testanimation from '/src/testanimation.json'


function Login({ onLogin }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isActive, setIsActive] = useState(false);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [signupPassword, setSignupPassword] = useState('');

    const [isRegistered, setIsRegistered] = useState(false);

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError(''); // Reset the error message on a new submit

        // Here you would call the authentication service
        try {
            await onLogin(username, password);
        } catch (err) {
            setError('Failed to login: ' + (err.response?.data?.message || err.message));
        }
    };
    const handleRegister = async (event) => {
        event.preventDefault(); // Prevent the default form submission

        try {
            const response = await fetch('http://localhost:8080/unlimitedmarketplace', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    userName: name,
                    email: email,
                    passwordHash: signupPassword
                })
            });

            if (response.ok) {
                // If the registration was successful, set isRegistered to true
                setIsRegistered(true);
                console.log('Registration successful');
            } else {
                // If there was an error, set the error state
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to register');
            }
        } catch (error) {
            console.error('Failed to register:', error);
            setError('Failed to register: ' + error.message);
            setIsRegistered(false); // Ensure the registration state is reset on error
        }
    };

    return (
        <div>
            {isRegistered ? (
                // Render the Lottie animation if registration was successful
                <Lottie animationData={testanimation} loop={false} />
            ) : (
                // Your existing form and login logic here
                <form onSubmit={handleRegister}>
                    {/* Your registration form fields */}
                </form>
            )}
        </div>
    );
}


export default Login;
