import React, { useState } from 'react';

function Login({ onLogin }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isActive, setIsActive] = useState(false);

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError(''); // Reset the error message on a new submit

        // Here you would call the authentication service
        try {
            await onLogin(username, password);
        } catch (err) {
            setError('Failed to login: ' + (err.response?.data?.message || err.message));
        }
    };

    return (
        <div className={`container ${isActive ? 'active' : ''}`} id="container"> {/* Toggle class based on isActive state */}
            <div className="form-container sign-up">
                {/* Your sign-up form goes here */}
            </div>
            <div className="form-container sign-in">
                {/* Your sign-in form goes here, using handleSubmit */}
            </div>
            <div className="toggle-container">
                <div className="toggle">
                    <div className="toggle-panel toggle-left">
                        {/* Content for returning users */}
                        <button className="ghost" id="login" onClick={() => setIsActive(false)}>Sign In</button>
                    </div>
                    <div className="toggle-panel toggle-right">
                        {/* Content for new users */}
                        <button className="ghost" id="register" onClick={() => setIsActive(true)}>Sign Up</button>
                    </div>
                </div>
            </div>
        </div>
    );

}

export default Login;
