import React, { useState } from 'react';
import Lottie from "lottie-react";
import testanimation from './testanimation.json';


function SignUpForm({ onRegister }) {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleRegister = async (event) => {
        event.preventDefault();
        onRegister(name, email, password);
    };

    return (
        <form onSubmit={handleRegister}>
            <h1>Create Account</h1>
            <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} />
            <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <button type="submit">Sign Up</button>
        </form>
    );
}

// SignIn form component
function SignInForm({ onLogin }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        onLogin(email, password);
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>Sign In</h1>
            <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
            <button type="submit">Sign In</button>
        </form>
    );
}

// Registration Animation component
function RegistrationAnimation({ show }) {
    if (!show) return null;

    return (
        <div style={{ width: '300px', height: '300px', position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: '9999' }}>
            <Lottie animationData={testanimation} loop={false} />
        </div>
    );
}

// Main Login component
function Login({ onLogin, onRegister }) {
    const [isActive, setIsActive] = useState(false);
    const [showAnimation, setShowAnimation] = useState(false);

    const handleRegister = (name, email, password) => {
        // Place your registration logic here
        // After successful registration:
        setShowAnimation(true);
        setTimeout(() => setShowAnimation(false), 5000);
    };

    return (
        <div className={`container ${isActive ? 'active' : ''}`} id="container">
            <RegistrationAnimation show={showAnimation} />
            <div className="form-container sign-up">
                <SignUpForm onRegister={handleRegister} />
            </div>
            <div className="form-container sign-in">
                <SignInForm onLogin={onLogin} />
            </div>
            <div className="toggle-container">
                <button className="ghost" id="signIn" onClick={() => setIsActive(false)}>Sign In</button>
                <button className="ghost" id="signUp" onClick={() => setIsActive(true)}>Sign Up</button>
            </div>
        </div>
    );
}

export default Login;
