import './App.css';
import Login from './Login'; // Import your Login component
import Lottie from "lottie-react";
import testanimation from "./testanimation.json";
import loginsuccessanimation from './testanimation.json';

import axios from 'axios'; // You might need to install axios via npm if you haven't already

function App() {
    const onLogin = async (username, password) => {
        try {
            const response = await axios.post('http://localhost:8080/unlimitedmarketplace/auth/login', {
                username,
                password
            });

            if (response && response.data) {
                console.log('Login successful', response.data);
                localStorage.setItem('token', response.data.token); // Adjust based on actual response structure
                setShowLoginSuccessAnimation(true); // Show the success animation
                setTimeout(() => setShowLoginSuccessAnimation(false), 5000); // Hide the animation after 5 seconds
            } else {
                throw new Error('Invalid server response');
            }
        } catch (error) {
            console.error('Login failed:', error);
            throw error;
        }
    };



    return (
        <div>
            <Login onLogin={onLogin}/>
        </div>
    );
}

export default App;
