import React, { useState } from 'react';
import Lottie from "lottie-react";
import testanimation from './testanimation.json';
import loginsuccessanimation from './testanimation.json';

function Login({ onLogin }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isActive, setIsActive] = useState(false);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [signupPassword, setSignupPassword] = useState('');

    const [isRegistered, setIsRegistered] = useState(false);
    const [showAnimation, setShowAnimation] = useState(false);
    const [showLoginSuccessAnimation, setShowLoginSuccessAnimation] = useState(false);

    const handleSubmit = async (event) => {
        event.preventDefault(); // Prevent default form submission
        setError(''); // Clear any existing errors

        try {
            await onLogin(username, password); // Try to log in
            // If no error was thrown, login is considered successful
            setShowLoginSuccessAnimation(true); // Display success animation
            setTimeout(() => setShowLoginSuccessAnimation(false), 5000); // Hide animation after 5 seconds
            // You can also redirect the user or perform other actions post-login here
        } catch (error) {
            // If an error is thrown, it means login was unsuccessful
            setError('Failed to login: ' + (error.response?.data?.message || error.message));
            // Ensure that the success animation is not shown in case of failure
            setShowLoginSuccessAnimation(false);
        }
    };


    function RegistrationAnimation({ show }) {
        if (!show) return null;

        return (
            <div style={{ width: '300px', height: '300px', position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: '9999' }}>
                <Lottie animationData={testanimation} loop={false} />
            </div>
        );
    }

    const handleRegister = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:8080/unlimitedmarketplace', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    userName: name,
                    email: email,
                    passwordHash: signupPassword
                })
            });

            if (response.ok) {
                setShowAnimation(true);  // This will trigger the animation
                setTimeout(() => setShowAnimation(false), 5000);  // This will hide the animation after 5 seconds

            } else {
                const errorData = await response.json();
                setError('Failed to register: ' + errorData.message);
            }
        } catch (error) {
            console.error('Failed to register:', error);
            setError('Failed to register: ' + error.message);
        }
    };



    return (
        <div className={`container ${isActive ? 'active' : ''}`} id="container">
            <RegistrationAnimation show={showAnimation} />
            {showLoginSuccessAnimation && (
                <div style={{ width: '300px', height: '300px', position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', zIndex: '9999' }}>
                    <Lottie animationData={loginsuccessanimation} loop={false} />
                </div>
            )}

            <div className="form-container sign-up">
                <form onSubmit={handleRegister}>
                    <h1>Create Account</h1>
                    <div className="social-icons">
                        {/* Social icons can remain as is, or you can implement OAuth functionality */}
                    </div>
                    <span>or use your email for registration</span>
                    <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)}/>
                    <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)}/>
                    <input type="password" placeholder="Password" value={signupPassword}
                           onChange={e => setSignupPassword(e.target.value)}/>
                    <button type="submit">Sign Up</button>
                </form>

            </div>
            <div className="form-container sign-in">
                <form onSubmit={handleSubmit}>
                    <h1>Sign In</h1>
                    <div className="social-icons">
                        <a href="#" className="icon"><i className="fa-brands fa-google-plus-g"></i></a>
                        <a href="#" className="icon"><i className="fa-brands fa-facebook-f"></i></a>
                        <a href="#" className="icon"><i className="fa-brands fa-github"></i></a>
                        <a href="#" className="icon"><i className="fa-brands fa-linkedin-in"></i></a>
                    </div>
                    <span>or use your email password</span>
                    <div className={"input-username"}>
                        <input
                            type="username"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className={"input-password"}>
                        <input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <a href="#">Forget Your Password?</a>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <button type="submit">Sign In</button>
                </form>
            </div>
            <div className="toggle-container">
                <div className="toggle">
                    <div className="toggle-panel toggle-left">
                        {/* Content for returning users */}
                        <button className="ghost" id="login" onClick={() => setIsActive(false)}>Sign In</button>
                    </div>
                    <div className="toggle-panel toggle-right">
                        {/* Content for new users */}
                        <button className="ghost" id="register" onClick={() => setIsActive(true)}>Sign Up</button>
                    </div>
                </div>
            </div>
        </div>
    );

}

export default Login;
