import React, { useState } from 'react';
import Lottie from "lottie-react";
import testanimation from './testanimation.json';


function Login({ onLogin }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isActive, setIsActive] = useState(false);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [signupPassword, setSignupPassword] = useState('');

    const [isRegistered, setIsRegistered] = useState(false);
    const [showAnimation, setShowAnimation] = useState(false);

    const handleSubmit = async (event) => {
        event.preventDefault();
        setError(''); // Reset the error message on a new submit

        // Here you would call the authentication service
        try {
            await onLogin(username, password);
        } catch (err) {
            setError('Failed to login: ' + (err.response?.data?.message || err.message));
        }
    };
    const handleRegister = async (event) => {
        event.preventDefault(); // Prevent the default form submission

        try {
            const response = await fetch('http://localhost:8080/unlimitedmarketplace', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    userName: name,
                    email: email,
                    passwordHash: signupPassword
                })
            });

            if (response.ok) { // Check if registration was successful
                const data = await response.json();
                console.log('Registration successful', data);
                setShowAnimation(true); // Show animation
                console.log('showAnimation set to', showAnimation); // Log to see if showAnimation is set to true
                if (showAnimation) {
                    console.log('Rendering animation');
                    return (
                        <div style={{ width: '300px', height: '300px' }}> {/* Adjust size as needed */}
                            <Lottie animationData={testanimation} loop={false} />
                        </div>
                    );
                }

                setTimeout(() => setShowAnimation(false), 5000); // Hide animation after 5 seconds
            } else {
                // If registration wasn't successful, handle it here
                const errorData = await response.json();
                setError('Failed to register: ' + errorData.message);
                setShowAnimation(false);
            }
        } catch (error) {
            console.error('Failed to register:', error);
            setError('Failed to register: ' + error.message);
            setShowAnimation(false);
        }
    };


    return (
        <div className={`container ${isActive ? 'active' : ''}`} id="container">
            {showAnimation && (
                <div style={{ width: '300px', height: '300px', backgroundColor: 'white' }}> {/* Adjust size as needed */}
                    <Lottie animationData={testanimation} loop={false} />
                </div>
            )}


            <div className="form-container sign-up">
                <form onSubmit={handleRegister}>
                    <h1>Create Account</h1>
                    <div className="social-icons">
                        {/* Social icons can remain as is, or you can implement OAuth functionality */}
                    </div>
                    <span>or use your email for registration</span>
                    <input type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)}/>
                    <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)}/>
                    <input type="password" placeholder="Password" value={signupPassword}
                           onChange={e => setSignupPassword(e.target.value)}/>
                    <button type="submit">Sign Up</button>
                </form>

            </div>
            <div className="form-container sign-in">
                <form onSubmit={handleSubmit}>
                    <h1>Sign In</h1>
                    <div className="social-icons">
                        <a href="#" className="icon"><i className="fa-brands fa-google-plus-g"></i></a>
                        <a href="#" className="icon"><i className="fa-brands fa-facebook-f"></i></a>
                        <a href="#" className="icon"><i className="fa-brands fa-github"></i></a>
                        <a href="#" className="icon"><i className="fa-brands fa-linkedin-in"></i></a>
                    </div>
                    <span>or use your email password</span>
                    <div className={"input-username"}>
                        <input
                            type="email"
                            placeholder="Email"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </div>
                    <div className={"input-password"}>
                        <input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <a href="#">Forget Your Password?</a>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <button type="submit">Sign In</button>
                </form>
            </div>
            <div className="toggle-container">
                <div className="toggle">
                    <div className="toggle-panel toggle-left">
                        {/* Content for returning users */}
                        <button className="ghost" id="login" onClick={() => setIsActive(false)}>Sign In</button>
                    </div>
                    <div className="toggle-panel toggle-right">
                        {/* Content for new users */}
                        <button className="ghost" id="register" onClick={() => setIsActive(true)}>Sign Up</button>
                    </div>
                </div>
            </div>
        </div>
    );

}

export default Login;
